export = pool;

/**
 * An allocator as used by {@link util.pool***REMOVED***.
 * @typedef PoolAllocator
 * @type {function***REMOVED***
 * @param {number***REMOVED*** size Buffer size
 * @returns {Uint8Array***REMOVED*** Buffer
 */
type PoolAllocator = (size: number) => Uint8Array;

/**
 * A slicer as used by {@link util.pool***REMOVED***.
 * @typedef PoolSlicer
 * @type {function***REMOVED***
 * @param {number***REMOVED*** start Start offset
 * @param {number***REMOVED*** end End offset
 * @returns {Uint8Array***REMOVED*** Buffer slice
 * @this {Uint8Array***REMOVED***
 */
type PoolSlicer = (this: Uint8Array, start: number, end: number) => Uint8Array;

/**
 * A general purpose buffer pool.
 * @memberof util
 * @function
 * @param {PoolAllocator***REMOVED*** alloc Allocator
 * @param {PoolSlicer***REMOVED*** slice Slicer
 * @param {number***REMOVED*** [size=8192] Slab size
 * @returns {PoolAllocator***REMOVED*** Pooled allocator
 */
declare function pool(alloc: PoolAllocator, slice: PoolSlicer, size?: number): PoolAllocator;
